mapper.select:
SELECT BOARD_SEQ AS boardSeq, TITLE AS title, WRITER AS writer, VIEW_COUNT AS viewCount, REG_DATE AS regDate FROM BOARD_TB
AND USE_YN = 'Y'
AND WRITER = #{currentUserName}
ORDER BY BOARD_SEQ DESC LIMIT #{firstIndex}, #{recordCountPerPage}

SELECT COUNT(*) AS cnt FROM BOARD_TB
AND USE_YN = 'Y'
AND WRITER = #{currentUserName}

SELECT BOARD_SEQ, TITLE, WRITER, VIEW_COUNT, REG_DATE, USE_YN FROM BOARD_TB
AND USE_YN = 'N'
ORDER BY BOARD_SEQ DESC LIMIT #{firstIndex}, #{recordCountPerPage}
